const formForSearch = {
  validate: false,
  name: 'search',
  title: 'Найти',
  buttonTextDefault: 'Найти',
  buttonTextLoading: 'Поиск...',
  inputs: [
    {
      type: 'search',
      name: 'search',
      placeholder: 'Фильм',
      required: true,
    },
    {
      type: 'checkbox',
      name: 'shorts',
      placeholder: 'Короткометражки',
      required: false,
      checked: false,
    },
  ]
};

export { formForSearch };
