import './MoviesCard.css';
import { useState } from 'react';

const MoviesCard = ({
  saved,
  movie,
}) => {
  const [ isLiked, setIsLiked] = useState(false);
  const likeButtonClassname = `movies__button_dislike ${
    isLiked && 'movies__button_like'
  }`;

  function handleLike() {
    setIsLiked(!isLiked);
  }

  return (
    <li className='movies__item'>
      <div className='movies__info'>
      <h2 className='movies__title'>{movie.nameRU}</h2>
      <button
          className={`movies__button ${ saved ? 'movies__button_delete' : likeButtonClassname }`}
          type='button'
          onClick={handleLike}
        />
        <p className='movies__duration'>
          {(movie.duration / 60) | 0}ч {movie.duration % 60}м
        </p>
      </div>
      <img
        className='movies__photo'
        src={movie.image}
        alt={movie.nameRU}
        onClick={handleLike}
      />
    </li>
  );
};

export default MoviesCard;
